mapper.select:
select s.id seatId, s.qunar_name seatName, bsm.busi_id bType, bsm.busi_supplier_id bSuId, bsm.bsuid_and_type bSuIdAndTypeId, bsm.supplier_id as supplierId, supplier.name as supplierName, supplier.logo_url as supplierLogo from busi_supplier_mapping bsm, seat s, supplier where bsm.supplier_id = s.supplier_id and supplier.id = s.supplier_id and s.status = 1
and bsm.bsuid_and_type in
#{bSuIdAndType}

select string_agg(qunar_name,',') from seat s,busi_supplier_mapping bbm where bbm.busi_id = #{busiType} and s.supplier_id = bbm.supplier_id and s.status = 1
and bbm.busi_supplier_id in
#{supplierId}

select string_agg(qunar_name,',') from seat s where s.status = 1
and supplier_id in
#{supplierId}

select string_agg(qunar_name,',') from seat s where status = 1
AND supplier_id in
#{supplierId}

SELECT , supplier.name AS supplierName FROM seat,supplier WHERE seat.status = 1
AND lower(seat.qunar_name) in
#{qunarName}
AND seat.supplier_id = supplier.id and supplier.id=#{supplierId}

SELECT sg.id, sg.name, sg.strategy, sg.supplier_id as supplierId
FROM seat_group sg , seat_group_mapping
where sg.id = seat_group_mapping.group_id and  seat_group_mapping.seat_id=#{seatId} and sg.supplier_id=#{supplierId}

select sgm.group_id as groupId,sgm.seat_id as seatId,bsgm.busi_id as busiId,sg.name as groupName from seat_group_mapping sgm left join busi_seat_group_mapping bsgm on sgm.group_id = bsgm.group_id left join seat_group sg on sgm.group_id = sg.id left join seat s on s.id = sgm.seat_id WHERE s.status = 1
and sgm.seat_id IN
#{seatId}
and sg.name IN
#{groupName}
and 1 = 0

SELECT
, sg.id AS groupId,sg.name AS groupName ,sg.strategy AS strategy ,service_status as serviceState FROM seat, seat_group_mapping sgm, seat_group sg, busi_seat_mapping bsm WHERE seat.status = 1
AND sg.id IN
#{groupId}
AND sg.id = sgm.group_id AND sgm.seat_id = seat.id AND bsm.seat_id = seat.id AND seat.supplier_id = sg.supplier_id and seat.status = 1
AND bsm.busi_id = #{busiId}
